import axios from "axios";
import { ServiceEnpoint } from "../../apiConstant";
import {
  FETCH_ALL_CAPSULES_SUCCESS,
  FETCH_ALL_CAPSULES_FAILURE,
  SHOW_INPROGRESS,
  FETCH_CAPSULE_SUCCESS,
  FETCH_CAPSULE_FAILURE,
} from "./capsuleActionTypes";
const showInProgress = () => ({
  type: SHOW_INPROGRESS
});
const fetchAllCapsulesSuccess = (capsules) => ({
  type: FETCH_ALL_CAPSULES_SUCCESS,
  payload: capsules,
});
const fetchAllCapsulesFailure = (errorMessage) => ({
  type: FETCH_ALL_CAPSULES_FAILURE,
  payload: { errorMessage },
});

const fetchCapsuleSuccess = (capsule) => ({
  type: FETCH_CAPSULE_SUCCESS,
  payload: capsule,
});

const fetchCapsuleFailure = (errorMessage) => ({
  type: FETCH_CAPSULE_FAILURE,
  payload: { errorMessage },
});
export const fetchCapsule = (capsuleId) => async (dispatch) => {
  try {
    const data = { launch: true };
    dispatch(showInProgress());
    const response = await axios.post(
      `${ServiceEnpoint.CAPSULE}/${capsuleId}`,
      data
    );
    const { capsule } = response;
    dispatch(fetchCapsuleSuccess(capsule));
  } catch (error) {
    dispatch(fetchCapsuleFailure(error.message));
  }
};

export const fetchAllCapsules = () => async (dispatch) => {
  try {
    dispatch(showInProgress());
    debugger;
    console.log(ServiceEnpoint.ALL_CAPSULES);
    const response = await axios.get(ServiceEnpoint.ALL_CAPSULES);
    const { capsules } = response;
    dispatch(fetchAllCapsulesSuccess(capsules));
  } catch (error) {
    dispatch(fetchAllCapsulesFailure(error.message));
  }
};
