import { FETCH_ALL_CAPSULES_SUCCESS, FETCH_ALL_CAPSULES_FAILURE, SHOW_INPROGRESS, FETCH_CAPSULE_FAILURE, FETCH_CAPSULE_SUCCESS } from './capsuleActionTypes';
const initialState = {
    allCapsules: [],
    isFetchingInProgress: false,
    isAllCapsulesFetchSuccess: false,
    isCurrCapsuleFetchSuccess: true,
    errorMessage: null,
    currCapsuleId: null
}

const reducer = (state = initialState, action) => {
    const { payload } = action;
    switch (action.type) {

        case SHOW_INPROGRESS:
            return { ...state, isFetchingInProgress: true, errorMessage: null };
        case FETCH_ALL_CAPSULES_SUCCESS:
            const { capsules } = payload;
            return { ...state, allCapsules: capsules, isAllCapsulesFetchSuccess: true, isFetchingInProgress: false, errorMessage: null };
        case FETCH_ALL_CAPSULES_FAILURE:
            debugger;
            return { ...state, allCapsules: [], isAllCapsulesFetchSuccess: false, isFetchingInProgress: false, errorMessage: payload.errorMessage };
        case FETCH_CAPSULE_SUCCESS:
            const { currCapsuleId } = payload;
            return { ...state, isCurrCapsuleFetchSuccess: true, currCapsuleId, isFetchingInProgress: false };
        case FETCH_CAPSULE_FAILURE:

            return { ...state, isCurrCapsuleFetchSuccess: false, currCapsuleId: null, isFetchingInProgress: false, errorMessage: payload.errorMessage };
        default:
            return state;
    }
}

export default reducer;
