import { Button, LandingPad as StyledComponent } from "./styles";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { fetchCapsule } from "../redux/capsules/capsulesActions";

const LandingPad = () => {
  const [isLauchEnabled, setIsLandingEnabled] = useState(false);
  const [capsuleText, setCapsuleText] = useState("");
  const dispatch = useDispatch();
  const onCapsuleTextChanged = ({ target }) => {
    const { value } = target;
    setCapsuleText(value);
    const isValid = RegExp('^[^&%$#]+$').test(value);
    console.log(isValid);
    setIsLandingEnabled(isValid);
  };
  const onLandingClicked = () => {

    dispatch(fetchCapsule(capsuleText));
  };
  return (
    <StyledComponent>
      <input onChange={onCapsuleTextChanged} />
      <Button disabled={!isLauchEnabled} onClick={onLandingClicked}>
        Landing Pad
      </Button>
    </StyledComponent>
  );
};
export default LandingPad;
